import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import Aos from "aos";
import "aos/dist/aos.css";

import logo from "./imgs/Logo.png";
import close from "./imgs/close.png";

const Navbar = () => {
  const [active, setActive] = useState("Pagina-principal");
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleToggle = () => {
    setToggle(!toggle);
  };

  return (
    <>
      <div data-aos="fade-down" className="fixed top-0 left-0 right-0 bg-[#282C33]">
        <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
          <div className="left flex gap-2 items-center font-bold text-white text-base">
            {/* logo */}
            <div className="img">
              <img src={logo} alt="" />
            </div>
            Daaviinchiii
          </div>
          <div className="right flex items-center">
            <div
              className={`menu duration-300 flex-col justify-center md:flex-row flex fixed w-full ${!toggle ? "right-[-100%] top-0 bottom-0" : "right-0 top-0 bottom-0"
                } bg-[#282C33] md:static`}
            >
              <NavLink to="/meu_portfolio/">
                <li
                  className={`text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4 ${active === "Pagina-principal" ? "text-[#C778DD] font-medium" : ""
                    }`}
                  onClick={() => setActive("Pagina-principal")}
                >
                  #Pagina-principal
                </li>
              </NavLink>
              <NavLink to="/about">
                <li
                  className={`text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4 ${active === "Sobre-mim" ? "text-[#C778DD] font-medium" : ""
                    }`}
                  onClick={() => setActive("Sobre-mim")}
                >
                  #Sobre-mim
                </li>
              </NavLink>
              <NavLink to="/contact">
                <li
                  className={`text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4 ${active === "Contactos" ? "text-[#C778DD] font-medium" : ""
                    }`}
                  onClick={() => setActive("Contactos")}
                >
                  #Contactos
                </li>
              </NavLink>
              <div onClick={() => setToggle(false)} className="close absolute block md:hidden right-3 top-3">
                <img src={close} alt="" />
              </div>
            </div>
            {/* CLOSE&OPEN btns */}
            <div onClick={handleToggle} className="">
              <div className="open block md:hidden w-8">
                <div className="w-100 h-[1px] my-2 rounded-r-3xl bg-[#D9D9D9]"></div>
                <div className="w-100 h-[1px] my-2 rounded-r-3xl bg-[#D9D9D9]"></div>
                <div className="w-100 h-[1px] my-2 rounded-r-3xl bg-[#D9D9D9]"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
