import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Aos from "aos";
import "aos/dist/aos.css";

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const toggleMenu = () => {
    setToggle(!toggle);
  };

  return (
    <div
      data-aos="fade-down"
      className={`fixed top-0 left-0 right-0 bg-[#282C33] ${toggle ? "h-screen" : ""
        }`}
    >
      <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
        <div className="left flex gap-2 items-center font-bold text-white text-base">
          <div className="img">
            <img src={require("./imgs/Logo.png")} alt="" />
          </div>
          Daaviinchiii
        </div>
        <div className="right flex items-center">
          <div
            className={`menu duration-300 flex-col justify-center md:flex-row flex md:static ${toggle ? "absolute inset-0" : ""
              }`}
          >
            <NavLink
              to="/meu_portfolio/"
              onClick={toggleMenu}
              className="nav-link"
            >
              Pagina-principal
            </NavLink>
            <NavLink
              to="/about"
              onClick={toggleMenu}
              className="nav-link"
            >
              Sobre-mim
            </NavLink>
            <NavLink
              to="/contact"
              onClick={toggleMenu}
              className="nav-link"
            >
              Contactos
            </NavLink>
          </div>
          <div className="menu-icon" onClick={toggleMenu}>
            <div className={`burger ${toggle ? "hidden" : "block"}`}></div>
            <div className={`close ${toggle ? "block" : "hidden"}`}></div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
