import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { FiMenu, FiX } from "react-icons/fi";
import { useMediaQuery } from "react-responsive";
import Aos from "aos";
import "aos/dist/aos.css";

const Navbar = () => {
  const [toggle, setToggle] = useState(false);
  const isMobile = useMediaQuery({ maxWidth: 767 }); // Define a largura m치xima para dispositivos m칩veis

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const toggleMenu = () => {
    setToggle(!toggle);
  };

  const closeMenu = () => {
    setToggle(false);
  };

  return (
    <>
      <div data-aos="fade-down" className="fixed top-0 left-0 right-0 bg-[#282C33]">
        <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
          <div className="left flex gap-2 items-center font-bold text-white text-base">
            <div className="img">
              <img src={require("./imgs/Logo.png")} alt="" />
            </div>
            Daaviinchiii
          </div>
          <div className="right flex items-center">
            {isMobile && (
              <div className="menu-icon" onClick={toggleMenu}>
                {toggle ? (
                  <FiX size={24} onClick={closeMenu} />
                ) : (
                  <FiMenu size={24} />
                )}
              </div>
            )}
            <div
              className={`menu duration-300 flex-col justify-center md:flex-row flex ${toggle ? "right-0 top-0 bottom-0" : "right-full top-0 bottom-0"
                } bg-[#282C33] md:static`}
            >
              <NavLink to="/meu_portfolio/" onClick={toggleMenu}>
                <li className="text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4">
                  <span className="text-[#C778DD] font-medium">#</span>Pagina-principal
                </li>
              </NavLink>
              <NavLink to="/about" onClick={toggleMenu}>
                <li className="text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4">
                  <span className="text-[#C778DD] font-medium">#</span>Sobre-mim
                </li>
              </NavLink>
              <NavLink to="/contact" onClick={toggleMenu}>
                <li className="text-[#ABB2BF] text-[32px] my-4 md:my-auto md:text-base list-none mx-4">
                  <span className="text-[#C778DD] font-medium">#</span>Contactos
                </li>
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
