import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Aos from "aos";
import 'aos/dist/aos.css';

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleToggle = () => {
    setToggle(!toggle);
  };

  return (
    <>
      <div
        data-aos='fade-down'
        className="fixed top-0 left-0 right-0 bg-[#282C33]"
      >
        <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
          <div className="left flex gap-2 items-center font-bold text-white text-base">
            {/* logo */}
            <div className="img">
              <img src={require("./imgs/Logo.png")} alt="" />
            </div>
            Daaviinchiii
          </div>
          <div className="right flex items-center">
            {/* Mobile Menu Button */}
            <div
              className="block md:hidden"
              onClick={handleToggle}
            >
              <img
                src={require("./imgs/menu.png")}
                alt="Menu"
                className="w-8 h-8 cursor-pointer"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {toggle && (
        <div className="bg-[#282C33] text-white py-4">
          <NavLink
            to="/meu_portfolio/"
            onClick={handleToggle}
            className="block px-4 py-2"
          >
            Pagina-principal
          </NavLink>
          <NavLink
            to="/about"
            onClick={handleToggle}
            className="block px-4 py-2"
          >
            Sobre-mim
          </NavLink>
          <NavLink
            to="/contact"
            onClick={handleToggle}
            className="block px-4 py-2"
          >
            Contactos
          </NavLink>
        </div>
      )}
    </>
  );
};

export default Navbar;
