import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Aos from "aos";
import "aos/dist/aos.css";

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleToggle = () => {
    setToggle(!toggle);
  };

  return (
    <>
      <div
        data-aos="fade-down"
        className="fixed top-0 left-0 right-0 bg-[#282C33]"
      >
        <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
          <div className="left flex gap-2 items-center font-bold text-white text-base">
            {/* logo */}
            <div className="img">
              <img src={require("./imgs/Logo.png")} alt="" />
            </div>
            Daaviinchiii
          </div>
          <div className="right flex items-center">
            {/* Hambúrguer Menu */}
            <div
              className="md:hidden"
              onClick={handleToggle}
              role="button"
              aria-label="Menu"
            >
              <div className={`menu-icon ${toggle ? "open" : ""}`}>
                <div className="bar"></div>
                <div className="bar"></div>
                <div className="bar"></div>
              </div>
            </div>

            {/* Menu de Navegação */}
            <div
              className={`menu duration-300 flex-col justify-center md:flex-row flex fixed w-full md:static ${toggle ? "" : "hidden"
                }`}
            >
              <NavLink
                to="/meu_portfolio/"
                onClick={handleToggle}
                activeClassName="active"
              >
                <span className="nav-link">#</span>Pagina-principal
              </NavLink>
              <NavLink
                to="/about"
                onClick={handleToggle}
                activeClassName="active"
              >
                <span className="nav-link">#</span>Sobre-mim
              </NavLink>
              <NavLink
                to="/contact"
                onClick={handleToggle}
                activeClassName="active"
              >
                <span className="nav-link">#</span>Contactos
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
