import { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import Aos from "aos";
import 'aos/dist/aos.css';

const Navbar = () => {
  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const [isMobile, setIsMobile] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const checkIsMobile = () => {
      const screenWidth = window.innerWidth;
      setIsMobile(screenWidth < 768);
    };

    checkIsMobile();

    window.addEventListener("resize", checkIsMobile);

    return () => {
      window.removeEventListener("resize", checkIsMobile);
    };
  }, []);

  return (
    <>
      <div
        data-aos='fade-down'
        className="fixed top-0 left-0 right-0 bg-[#282C33]"
      >
        <div className="px-5 max-w-[1560px] mx-auto flex items-center justify-between pt-6 my-2">
          <div className="left flex gap-2 items-center font-bold text-white text-base">
            {/* logo */}
            <div className="img">
              <img src={require("./imgs/Logo.png")} alt="" />
            </div>
            Daaviinchiii
          </div>
          {isMobile && (
            <div className="right flex items-center md:hidden">
              <button
                className="text-white text-2xl focus:outline-none"
                onClick={handleMenuToggle}
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 6h16M4 12h16M4 18h16"
                  />
                </svg>
              </button>
            </div>
          )}
        </div>
      </div>

      {isMobile && menuOpen && (
        <div className="fixed top-0 left-0 right-0 bottom-0 bg-[#282C33] text-white">
          <div className="px-5 max-w-[1560px] mx-auto flex flex-col items-center justify-center pt-20">
            <NavLink
              to="/meu_portfolio/"
              className="text-[#ABB2BF] text-4xl my-4 md:text-base list-none"
              onClick={handleMenuToggle}
            >
              <span className="text-[#C778DD] font-medium">#</span>Pagina-principal
            </NavLink>
            <NavLink
              to="/about"
              className="text-[#ABB2BF] text-4xl my-4 md:text-base list-none"
              onClick={handleMenuToggle}
            >
              <span className="text-[#C778DD] font-medium">#</span>Sobre-mim
            </NavLink>
            <NavLink
              to="/contact"
              className="text-[#ABB2BF] text-4xl my-4 md:text-base list-none"
              onClick={handleMenuToggle}
            >
              <span className="text-[#C778DD] font-medium">#</span>Contactos
            </NavLink>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
